patch:
--- src/diagnostic.cpp
@@ -2,6 +2,7 @@
 // See end of file for extended copyright information.

 #include <cstring>
+#include <quick-lint-js/cpp.h>
 #include <quick-lint-js/diagnostic.h>
 #include <quick-lint-js/error.h>
 #include <quick-lint-js/gmo.h>
@@ -10,106 +11,111 @@
 #include <utility>

 namespace quick_lint_js {
-// Convert a legacy error_formatter user into a diagnostic_info.
+namespace {
+constexpr void strcpy(char* out, const char* in) noexcept {
+  while ((*out++ = *in++) != '\0')
+    ;
+}
+
+// Convert a QLJS_ERROR_TYPE user into a diagnostic_info.
 template <class Error>
 class diagnostic_info_builder {
  public:
-  constexpr explicit diagnostic_info_builder(const char* code,
-                                             const void* diagnostic)
-      : diagnostic_(diagnostic) {
-    std::strcpy(this->info_.code, code);
-  }
-
-  template <class... Args>
-  constexpr diagnostic_info_builder& warning(const gmo_message& message,
-                                             Args&... parameters) {
-    this->add(diagnostic_severity::warning, message,
-              std::forward<Args>(parameters)...);
-    return *this;
-  }
-
-  template <class... Args>
-  constexpr diagnostic_info_builder& error(const gmo_message& message,
-                                           Args&... parameters) {
-    this->add(diagnostic_severity::error, message,
-              std::forward<Args>(parameters)...);
-    return *this;
+  constexpr explicit diagnostic_info_builder(const char* code) {
+    strcpy(this->info_.code, code);
   }

+  // Each of Args must be a diagnostic_message_arg_info.
   template <class... Args>
-  constexpr diagnostic_info_builder& note(const gmo_message& message,
-                                          Args&... parameters) {
-    this->add(diagnostic_severity::note, message,
-              std::forward<Args>(parameters)...);
-    return *this;
-  }
-
-  constexpr diagnostic_info build() noexcept { return this->info_; }
-
- private:
-  template <class... Args>
-  void add(diagnostic_severity sev, const gmo_message& message, Args&... args) {
+  constexpr diagnostic_info_builder add(diagnostic_severity sev,
+                                        const gmo_message& message,
+                                        const Args&... arg_infos) {
     diagnostic_message_info& message_info =
         this->info_.messages[this->current_message_index_++];
     message_info.format = message;
     message_info.severity = sev;

     int current_arg_index = 0;
-    auto add_arg = [&](auto& arg_value) {
-      diagnostic_message_arg_info& arg_info =
-          message_info.args[current_arg_index++];
-      arg_info.type = arg_type(arg_value);
-      arg_info.offset = narrow_cast<std::uint8_t>(
-          reinterpret_cast<const char*>(&arg_value) -
-          reinterpret_cast<const char*>(this->diagnostic_));
-    };
-    (add_arg(args), ...);
-  }
+    ((message_info.args[current_arg_index++] = arg_infos), ...);

-  static diagnostic_arg_type arg_type(char8) noexcept {
-    return diagnostic_arg_type::char8;
-  }
-  static diagnostic_arg_type arg_type(identifier) noexcept {
-    return diagnostic_arg_type::identifier;
-  }
-  static diagnostic_arg_type arg_type(source_code_span) noexcept {
-    return diagnostic_arg_type::source_code_span;
-  }
-  static diagnostic_arg_type arg_type(statement_kind) noexcept {
-    return diagnostic_arg_type::statement_kind;
+    return *this;
   }

-  const void* diagnostic_;
-  diagnostic_info info_;
+  constexpr diagnostic_info build() noexcept { return this->info_; }
+
+ private:
+  diagnostic_info info_{};
   int current_message_index_ = 0;
 };

+template <class ArgType>
+constexpr diagnostic_arg_type get_diagnostic_message_arg_type() noexcept;
+template <>
+constexpr diagnostic_arg_type
+get_diagnostic_message_arg_type<char8>() noexcept {
+  return diagnostic_arg_type::char8;
+}
+template <>
+constexpr diagnostic_arg_type
+get_diagnostic_message_arg_type<identifier>() noexcept {
+  return diagnostic_arg_type::identifier;
+}
+template <>
+constexpr diagnostic_arg_type
+get_diagnostic_message_arg_type<source_code_span>() noexcept {
+  return diagnostic_arg_type::source_code_span;
+}
+template <>
+constexpr diagnostic_arg_type
+get_diagnostic_message_arg_type<statement_kind>() noexcept {
+  return diagnostic_arg_type::statement_kind;
+}
+
+template <class ArgType>
+constexpr diagnostic_message_arg_info make_diagnostic_message_arg_info(
+    std::uint8_t offset) noexcept {
+  return diagnostic_message_arg_info{
+      .offset = offset,
+      .type = get_diagnostic_message_arg_type<ArgType>(),
+  };
+}
+
 template <class Error>
-struct diagnostic_info_for_error_detail;
-
-// Convert a legacy error_formatter user into a diagnostic_info.
-#define QLJS_ERROR_TYPE(name, code, struct_body, format_call)               \
-  template <>                                                               \
-  struct diagnostic_info_for_error_detail<name> : public name {             \
-    constexpr diagnostic_info build_impl() const noexcept {                 \
-      return diagnostic_info_builder<name>(code, this) format_call.build(); \
-    }                                                                       \
-                                                                            \
-    static diagnostic_info build() noexcept {                               \
-      union U {                                                             \
-        U() {}                                                              \
-        diagnostic_info_for_error_detail instance;                          \
-      } u;                                                                  \
-      /* HACK(strager): This is probably undefined behavior. */             \
-      return u.instance.build_impl();                                       \
-    }                                                                       \
+struct diagnostic_info_for_error;
+
+#define MAKE_ARGS(...) MAKE_ARGS_N(QLJS_COUNT_ARGS(__VA_ARGS__), __VA_ARGS__)
+#define MAKE_ARGS_N(...) MAKE_ARGS_N_(__VA_ARGS__)
+#define MAKE_ARGS_N_(count, ...) MAKE_ARGS_##count(__VA_ARGS__)
+
+#define MAKE_ARGS_1(arg0)                                        \
+  make_diagnostic_message_arg_info<decltype(error_class::arg0)>( \
+      offsetof(error_class, arg0))
+#define MAKE_ARGS_2(arg0, arg1) MAKE_ARGS_1(arg0), MAKE_ARGS_1(arg1)
+#define MAKE_ARGS_3(arg0, arg1, arg2) MAKE_ARGS_2(arg0, arg1), MAKE_ARGS_1(arg2)
+
+#define ERROR(message_format, ...) \
+  .add(diagnostic_severity::error, message_format, MAKE_ARGS(__VA_ARGS__))
+#define WARNING(message_format, ...) \
+  .add(diagnostic_severity::warning, message_format, MAKE_ARGS(__VA_ARGS__))
+#define NOTE(message_format, ...) \
+  .add(diagnostic_severity::note, message_format, MAKE_ARGS(__VA_ARGS__))
+
+#define QLJS_ERROR_TYPE(name, code, struct_body, format_call)         \
+  template <>                                                         \
+  struct diagnostic_info_for_error<name> {                            \
+    using error_class = name;                                         \
+                                                                      \
+    static constexpr diagnostic_info get() noexcept {                 \
+      return diagnostic_info_builder<name>(code) format_call.build(); \
+    }                                                                 \
   };
 QLJS_X_ERROR_TYPES
 #undef QLJS_ERROR_TYPE
+}

-const diagnostic_info all_diagnostic_infos[] = {
+constexpr const diagnostic_info all_diagnostic_infos[] = {
 #define QLJS_ERROR_TYPE(name, code, struct_body, format_call) \
-  diagnostic_info_for_error_detail<name>::build(),
+  diagnostic_info_for_error<name>::get(),
     QLJS_X_ERROR_TYPES
 #undef QLJS_ERROR_TYPE
 };
--- src/quick-lint-js/cpp.h
@@ -7,6 +7,9 @@
 #define QLJS_CPP_CONCAT(x, y) QLJS_CPP_CONCAT_(x, y)
 #define QLJS_CPP_CONCAT_(x, y) x##y

+#define QLJS_COUNT_ARGS(...) QLJS_COUNT_ARGS_(__VA_ARGS__, 5, 4, 3, 2, 1, 0)
+#define QLJS_COUNT_ARGS_(_0, _1, _2, _3, _4, count, ...) count
+
 #endif

 // quick-lint-js finds bugs in JavaScript programs.
--- src/quick-lint-js/diagnostic.h
@@ -41,8 +41,6 @@ struct diagnostic_info {
   diagnostic_message_info messages[2];
 };

-extern const diagnostic_info all_diagnostic_infos[];
-
 const diagnostic_info &get_diagnostic_info(error_type) noexcept;
 }

