       return tl::unexpected(strerror(errno));
     }
     size_hint = de.size();
   }
+#endif

   if (size_hint > std::numeric_limits<size_t>::max() / 4) {
     // Too large value on a 32-bit system won't work well, better bail.
@@ -255,6 +262,15 @@ read_file(const fs::path& path, size_t size_hint)
     return tl::unexpected(strerror(errno));
   }

