-      } while (!worker_.GetExit());
+        // Exit or reset the flag and return to WorkerLoop with a new config.
+        if (worker_.GetExit() == Exit::kThread) break;
+        worker_.SetExit(Exit::kNone);
+      }

       worker_.GetProfiler().SetGlobalIdx(~size_t{0});

