patch:
--- core/src/RegressionLine.h
@@ -79,6 +79,7 @@ class RegressionLine
 	auto signedDistance(PointF p) const { return dot(normal(), p) - c; }
 	template <typename T> auto distance(PointT<T> p) const { return std::abs(signedDistance(PointF(p))); }
 	PointF project(PointF p) const { return p - signedDistance(p) * normal(); }
+	PointF centroid() const { return std::accumulate(_points.begin(), _points.end(), PointF()) / _points.size(); }

 	void reset()
 	{
@@ -119,7 +120,7 @@ class RegressionLine
 				if (old_points_size == points.size())
 					break;
 #ifdef PRINT_DEBUG
-				printf("removed %zu points\n", old_points_size - points.size());
+				printf("removed %zu points -> %zu remaining\n", old_points_size - points.size(), points.size());
 #endif
 				ret = evaluate(points);
 			}
--- core/src/datamatrix/DMDetector.cpp
@@ -557,19 +557,23 @@ class EdgeTracer : public BitMatrixCursorF
 		return line.evaluate(1.5) && updateDirectionFromOrigin(p - line.project(p) + line.points().front());
 	}

+	bool updateDirectionFromLineCentroid(RegressionLine& line)
+	{
+		// Basically a faster, less accurate version of the above without the line evaluation
+		return updateDirectionFromOrigin(line.centroid());
+	}
+
 	bool traceLine(PointF dEdge, RegressionLine& line)
 	{
 		line.setDirectionInward(dEdge);
 		do {
 			log(p);
 			line.add(p);
-			if (line.points().size() % 50 == 10 && !updateDirectionFromLine(line))
+			if (line.points().size() % 50 == 10 && !updateDirectionFromLineCentroid(line))
 				return false;
 			auto stepResult = traceStep(dEdge, 1, line.isValid());
-			if (stepResult != StepResult::FOUND) {
-				updateDirectionFromLine(line);
-				return stepResult == StepResult::OPEN_END && line.points().size() > 1;
-			}
+			if (stepResult != StepResult::FOUND)
+				return stepResult == StepResult::OPEN_END && line.points().size() > 1 && updateDirectionFromLineCentroid(line);
 		} while (true);
 	}

